 BEGIN                                                                                        
                                    
  DELETE FROM UPA_PAINEL                                                                                        
  WHERE ACO_CODIGO = @ACO_CODIGO                                          
                                                                                  
  DELETE FROM UPA_FILA_SEQUENCIAL                                              
  WHERE ACO_CODIGO = @ACO_CODIGO                                                                                        
                                                                                  
  DELETE FROM UPA_FILA                                                                                        
