   VALUES (@ACO_CODIGO, GETDATE(), 10, @LOCAL_DESTINO, @ESPECIALIDADE_CODIGO, @USUARIO_CODIGO)                                                                                        
                        
   IF(@ORIGEM IS NOT NULL AND UPPER(@ORIGEM) = 'D')                        
   BEGIN                        
                          
  DECLARE @WDATA_AUX SMALLDATETIME                        
