  WHERE ACO_CODIGO = @ACO_CODIGO                                                
                                                                                 
  -- Atualiza o numero de chamadas                                                                                  
  UPDATE UPA_FILA SET                                     
   NUM_CHAMADAS = 0,                                                                                  
   DATA_CONFIRMACAO = GETDATE()                                                                                  
