   AND (AM.TIPSAI_CODIGOENCAMINHAMENTO <> 5 OR AM.TIPSAI_CODIGOENCAMINHAMENTO  IS NULL)--AM.TIPSAI_CODIGOENCAMINHAMENTO NOT IN (5)                                                                           
