 -- Seleciona os pacientes agendados no dia                                                                 -- ///////////////////////////////////////////////////////////////////////////////////////////////////////////////                                
   
    
      
           
 if @opcao = 25                                                                                  
 BEGIN                                                                     
                                                                                 
 SELECT agd_sequencial,                                                                          
   agd_datahora,                        
   CASE WHEN p.pac_nome_social is not null THEN                         
  p.pac_nome_social + '[' + p.pac_nome + ']'                        
