   ,F.ACO_CODIGO                                                                  
   ,F.NOME                                                                                   
   ,F.RISCO                                                                                  
   ,LA.SET_DESCRICAO AS LOC_DESCRICAO                          
   ,F.ACO_DATAHORA AS DATAHORA                                                                                  
   ,F.SEQUENCIAL                                                                                  
   ,F.CHAMADA_ATIVA                                                     
   ,F.DESTINO                                                                                  
   ,UE.EMER_CODIGO_DESTINO AS CODIGO_DESTINO                                                                                  
