		select top 1 * from RelatorioAssinado
		where CodigoRelatorio = @CodigoRelatorio 
		and ((@CodigoPrescricao is not null and CodigoPrescricao = @CodigoPrescricao)
		     or (@CodigoPrescricao is null and CodigoPrescricao is null))
